import {format} from "date-fns";
import {Link} from "react-router-dom";

const Post = (title,summary,cover,content,createdAt) => {
    return ( 
        <div className="post">
        <div className="image">
          <img src="https://orhanergun.net/uploads/blog/thumbnail/BGP-Route-Advertisement.jpg" alt="BGP Route Advertisment"></img>
        </div>
        <div className="texts">
          <h2>{title}</h2>
          <p className="info">
            <span className="author">Stanley Arvey</span>
            <time>{format(new Date(createdAt))}</time>
          </p>
          <p className="summary">In a networking context, BGP route advertisement refers to the process of broadcasting routing information to neighboring routers. This is an important aspect of the Border Gateway Protocol (BGP), which is used for exchanging routing and reachability information among autonomous systems on the internet.</p>
        </div>
      </div>
     );
}
 
export default Post;
