import ReactQuill from "react-quill";
import 'react-quill/dist/quill.snow.css';

const CreatePost = () => {
    return ( 
        <form onSubmit={createNewPost}>
            <input type="title"
                    placeholder={'Title'}
                    />
            <input type="summary"
                    placeholder={'Summary'}
                    />
            <input type="file"/>
            <ReactQuill/>
        </form>
    );
}
 
export default CreatePost;
